import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { ProductEntity } from 'src/app/interface/productEntity';
import { SearchModel } from 'src/app/interface/searchModel';
import { AuthServicio } from 'src/app/servicios/auth.service';
import { productServicio } from 'src/app/servicios/product.service';

@Component({
  selector: 'app-nav-bar',
  templateUrl: 'nav-bar.component.html',
})
export class NavBarComponent implements OnInit{

  constructor(
    private _auth: AuthServicio,
    private _productService: productServicio,
    private _router: Router) {}

  ImagenLogo =  "../../../../assets/images/Convertic.JPG";
  aBuscar= "";
  profileMsg: string = "Mi perfil";
  public searchInfo: SearchModel = {
    products: [], active: false 
  };
  @Output() info: EventEmitter<SearchModel> = new EventEmitter();

  ngOnInit(): void {
    this._auth.session;
  }

  oneKeyup(){
    if (this.aBuscar != '') {
      this._productService.searchresults(this.aBuscar).
        subscribe((results: ProductEntity[])=>{
          this.searchInfo.products = results;
          this.searchInfo.active = true;
          this.info.emit(this.searchInfo);
        }).add()
    }else{
      this.searchInfo.products = [];
      this.searchInfo.active = false;
      this.info.emit(this.searchInfo);
    }
  }
  
  toCart(){
    this._router.navigate(['Tienda/carrito']); 
  }

  closeSession(){
    this._auth.cerrarSesion();
  }
}
<nav class="navbar navbar-expand-lg navbar-light p-3" style="background-color: DeepSkyBlue">
  <div class="container-fluid">
    <a class="navbar-brand" [routerLink]="['/Tienda/home', 'man']" >
      <img src={{ImagenLogo}} width="80" height="30" alt="Foto producto" class="rounded-3">
    </a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0 ">
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle fw-bold" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Género
          </a>
          <ul class="dropdown-menu">
            <li><a class="dropdown-item" [routerLink]="['/Tienda/home', 'man']">Hombre</a></li>
            <li><a class="dropdown-item" [routerLink]="['/Tienda/home', 'woman']">Mujer</a></li>
            <li><hr class="dropdown-divider"></li>
            <li><a class="dropdown-item" href="#">Categorias</a></li>
          </ul>
        </li>
        <li class="nav-item">
          <a class="nav-link fw-bold" [routerLink]="['/Product/carrito']">Carrito</a>
        </li>
      </ul>
      <form class="d-flex" role="search">
        <input class="form-control me-2 text-lowercase" type="search" placeholder="Search" aria-label="Search" [(ngModel)] = aBuscar (keyup)="oneKeyup()" name = "submitSearch"/>
      </form>
      <ul class="navbar-nav mb-2 mb-lg-0 ">
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle fw-bold" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Mi perfil
          </a>
          <ul class="dropdown-menu">
            <li><a class="dropdown-item" data-bs-toggle="modal" data-bs-target="#MiData">Actualizar</a></li>
            <li><a class="dropdown-item" (click)="closeSession()">Cerrar sesion</a></li>
          </ul>
        </li>
      </ul>
    </div>
  </div>
</nav>
<hr class="border border-info">
<div class="modal fade" id="MiData" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-lg modal-dialog-centered modal-dialog-scrollable" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title text-capitalize" id="exampleModalLabel text-capitalize"></h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <app-profile-description></app-profile-description>
      </div> 
      <div class="modal-footer">
        <p class="fs-3 text-center fw-semibold">Convetic<span class="fw-bold" style="color: MediumTurquoise;">Shop</span></p>
      </div>
    </div>
  </div>
</div>
