import { Component, Input } from '@angular/core';
import { HomeGridProduct } from 'src/app/interface/homeGridPriduct';

@Component({
  selector: 'app-product-grid',
  templateUrl: './product-grid.component.html',
  styleUrls: ['./product-grid.component.css']
})
export class ProductGridComponent {

  rutaImagen = "../../../../assets/images/No_disponible.jpg";

  @Input() mainList?: HomeGridProduct;
}
<div class="card mb-3" style="max-width: 540px;">
  <div class="row g-0">
    <div class="col-md-4">
      <img src= {{mainList?.rutaFoto}} class="img-fluid rounded-start" alt="...">
    </div>
    <div class="col-md-8">
      <div class="card-body">
        <h5 class="card-title">{{mainList?.producto}</h5>
        <p class="card-text">{{mainList?.descripcion}} as a natural lead-in to additional content. This content is a little bit longer.</p>
        <p class="card-text"><small class="text-muted">{{mainList?.genero}}</small></p>
      </div>
    </div>
  </div>
</div>
