import { Component, OnInit} from '@angular/core';
import {ActivatedRoute} from '@angular/router';
import { productServicio } from 'src/app/servicios/product.service';
import { DetailResponse } from 'src/app/interface/detailResponse';
import { Color } from 'src/app/interface/color';
import { Size } from 'src/app/interface/size';
import { ProductDetailed } from 'src/app/interface/productDetailed';
import { ProductStock } from 'src/app/interface/productStock';

@Component({
  selector: 'app-product-description',
  templateUrl: './product-description.component.html',
  styleUrls: ['./product-description.component.css']
})
export class ProductDescriptionComponent {

  productId:number = 0
  productImage = "../../../../assets/images/No_disponible.jpg";
  colors: Color[] =[];
  sizes: Size[] =[];
  stocks: ProductStock[] =[];
  description: ProductDetailed = {
      plu: '',
      nombre: '',
      fabricante: '',
      precio: 0,
      descripcion: '',
      rutaForo: "../../../../assets/images/No_disponible.jpg"
  }

  constructor(
    private route: ActivatedRoute,
    private _service: productServicio
  ) {  }

ngOnInit(){
  this.productId = this.route.snapshot.params['id'];
  this.productDetail();
}

productDetail(){
this._service.ProductDetail(this.productId).
  subscribe((response: DetailResponse)=>{
    this.colors = response.color;
    this.sizes = response.size;
    this.description = response.product;
    this.stocks = response.stock;
  }).add()
}
}
