import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Login } from 'src/app/interface/login';
import { RespuestaApi } from 'src/app/interface/respuestaapi';
import { AuthServicio } from 'src/app/servicios/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  public login: Login = { correo: '', clave: '' }

  constructor(private _servicio: AuthServicio, private _router: Router) {

  }

  onSubmit() {
    this._servicio.Login(this.login)
      .subscribe((response: RespuestaApi) => {
        if (response.state) {
          this._servicio.session = response;  // Asigna información del token
      }
        this._router.navigate(['Tienda/home/man']);
      }).add(() => {

      });
  }

  OnSignIn(){
    this._router.navigate(['Auth/registro']);
  }
}
  <div class="container text-center border rounded-5 p-4 bg-body-secondary">
      <div class="col-auto opacity-75">
      <form>
          <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label"> <b>Correo electrónico.</b></label>
            <input name="correo" type="email" [(ngModel)] = login.correo class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" >
          </div>
          <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label"> <b>Contraseña.</b></label>
            <input name="clave"  type="password" class="form-control" id="exampleInputPassword1" [(ngModel)] = login.clave>
          </div>
          <div class="mb-3 form-check">
            <input type="checkbox" class="form-check-input" id="exampleCheck1" >
            <label class="form-check-label" for="exampleCheck1">Darme de baja.</label>
          </div>
          <button type="button" class="btn btn-info btn-lg" (click)="onSubmit()">Submit</button>
          <div class="sing-up">            
            <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
              Launch demo modal
            </button>
          </div>
          <a class="text-reset" [routerLink]="['//registro']">No estas registrado? <span>REGISTRATE AQUÍ</span></a>
      </form>
      </div>
      
      
      <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div class="modal-dialog modal-dialog-centered modal-dialog-scrollable" role="document">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
              <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div class="modal-body">
              <app-registro></app-registro>
            </div> 
            <div class="modal-footer">
              <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
              <button type="button" class="btn btn-primary">Save changes</button>
            </div>
          </div>
        </div>
      </div>



  </div>
