@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css']
})
export class BannerComponent {

}
<div id="intro" class="" style="background-image: url('../../../../../../assets/images/Banner.jpg'); background-size: cover; height: 25%;">
    <div class="row">
        <div class="col-sm-6 col-lg-9 text-center p-4 text-white">
            <h1>Encuentra la vestimenta perfecta.</h1>
        </div>
        <div class="col-sm-6 col-lg-3">
            <button type="button" class="btn btn-primary">Ver categorias</button>
        </div>
    </div>
</div>
