import { Component, Input } from '@angular/core';
import { ProductEntity } from 'src/app/interface/productEntity';

@Component({
  selector: 'app-best-sells',
  templateUrl: './best-sells.component.html',
  styleUrls: ['./best-sells.component.css']
})
export class BestSellsComponent {

  rutaImagen = "../../../../assets/images/No_disponible.jpg";

  @Input() productP?: ProductEntity;
}

  <div class="card p-2 m-2" style="width: 18rem;">
    <img class="card-img-top" src={{productP?.rutaFoto}} alt="Card image cap">
    <div class="card-body">
      <h5 class="card-title">{{productP?.producto}}</h5>
      <p class="card-text">{{productP?.descripcion}}.</p>
      <a  [routerLink]="['/Tienda/detail', productP?.id]" class="btn btn-primary">Ir al producto</a>
    </div>
  </div>
