import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Login } from 'src/app/interface/login';
import { RespuestaAuth } from 'src/app/interface/respuestaAuth';
import { AuthServicio } from 'src/app/servicios/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  public login: Login = { correo: '', clave: '' }

  constructor(private _servicio: AuthServicio, private _router: Router) 
  { }

  onSubmit() {
    this._servicio.Login(this.login)
      .subscribe((respuesta: RespuestaAuth) => {
        console.log(respuesta);
        this._router.navigate(['Tienda/home']);
      }).add(() => {

      });
  }

  OnSignIn(){
    this._router.navigate(['Auth/registro']);
  }
}
<div class="formulario">
    <form>
        <div class="mb-3">
          <label for="exampleInputEmail1" class="form-label" [(ngModel)]="login.correo">Correo o usuario</label>
          <input type="email" [ngModel]="" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
          <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
        </div>
        <div class="mb-3">
          <label for="exampleInputPassword1" class="form-label" [(ngModel)]="login.clave">Contraseña</label>
          <input name="clave"  type="password" class="form-control" id="exampleInputPassword1">
        </div>
        <div class="mb-3 form-check">
          <input type="checkbox" class="form-check-input" id="exampleCheck1">
          <label class="form-check-label" for="exampleCheck1">Darme de baja</label>
        </div>
        <button type="submit" class="btn btn-primary" (click)="onSubmit()">Ingresar</button>
        <div class="sing-in">
          <a href="">No estas registrado? <span>REGISTRATE AQUÍ</span></a>
        </div>
      
      </form>
    
</div>
