import { Component, Input } from '@angular/core';
import { HomeGridProduct } from 'src/app/interface/homeGridPriduct';

@Component({
  selector: 'app-product-grid',
  templateUrl: './product-grid.component.html',
  styleUrls: ['./product-grid.component.css']
})
export class ProductGridComponent {

  rutaImagen = "../../../../assets/images/No_disponible.jpg";

  @Input() mainList?: HomeGridProduct;
}

<div class="card rounded-0" style="width: 18rem;">
  <img src={{mainList?.rutaFoto}} class="card-img-top" alt="Foto Producto">
  <div class="card-body">
    <h5 class="card-title fs-3 fw-bold text-primary-emphasis">{{mainList?.producto}}</h5>
    <p class="card-text fs-5 fw-normal">{{mainList?.descripcion}}</p>
  </div>
  <ul class="list-group list-group-flush">
    <li class="list-group-item fw-4">codigo: {{mainList?.plu}}</li>
    <li class="list-group-item fw-4">{{mainList?.genero}}</li>
    <li class="list-group-item fw-4">{{mainList?.precio}} $COP</li>
  </ul>
  <div class="card-body">
    <a  [routerLink]="['/Tienda/detail', mainList?.id]" class="btn btn-primary">Ir al producto</a>
  </div>
</div>
